#!/bin/bash

# Update first
yum -y update

# Set build date
date > /etc/box_build

# Create Vagrant Mountpoint
mkdir /vagrant

# Install Vagrant SSH key
mkdir -pm 700 /home/vagrant/.ssh
echo "ssh-rsa  vagrant insecure public key" > /home/vagrant/.ssh/authorized_keys
chmod 0600 /home/vagrant/.ssh/authorized_keys && chown -R vagrant:vagrant /home/vagrant/.ssh

# Clean the system
rm -rf /tmp/*
yum -y clean all

#disable reverse dns lookups on sshd
echo "UseDNS no" >> /etc/ssh/sshd_config

exit 0
